(138, 129, 12, 2, 0, '198.86.248.2', 1306513277, 0, 1, 1, 1, 1, '', 'Clan code', '<r><URL url=\"https://www.moparscape.org/smf/index.php/topic,437244.msg3212956.html#msg3212956\"><LINK_TEXT text=\"https://www.moparscape.org/smf/index.ph ... msg3212956\">https://www.moparscape.org/smf/index.php/topic,437244.msg3212956.html#msg3212956</LINK_TEXT></URL><br/>\n<br/>\n<URL url=\"https://www.moparscape.org/smf/index.php/topic,397214.msg2949227.html#msg2949227\"><LINK_TEXT text=\"https://www.moparscape.org/smf/index.ph ... msg2949227\">https://www.moparscape.org/smf/index.php/topic,397214.msg2949227.html#msg2949227</LINK_TEXT></URL><br/>\n<br/>\n<URL url=\"http://dargaming.net/elite/board/viewtopic.php?f=12&amp;t=84&amp;p=85&amp;hilit=clan#p85\"><LINK_TEXT text=\"http://dargaming.net/elite/board/viewto ... t=clan#p85\">http://dargaming.net/elite/board/viewtopic.php?f=12&amp;t=84&amp;p=85&amp;hilit=clan#p85</LINK_TEXT></URL><br/>\n<br/>\n<URL url=\"http://pastebin.com/kzKbi1BM\">http://pastebin.com/kzKbi1BM</URL></r>', 'a8adeb046be861012395e6ae422ad4f3', 0, '', 'wpubwe8y', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(144, 132, 12, 2, 0, '65.190.15.53', 1306583364, 0, 1, 1, 1, 1, '', 'Kryptix irc bot', '<r><CODE><s>[code]</s>package org.rscemulation.server;\n\nimport org.jibble.pircbot.PircBot;\nimport org.jibble.pircbot.User;\nimport org.jibble.pircbot.Colors;\n\nimport org.rscemulation.server.model.Player;\nimport org.rscemulation.server.model.World;\n\npublic class IRC extends PircBot implements Runnable {\n        long lastCommand = 0;\n       \n    public void run() {\n        if (Config.IRC) {\n                Thread.currentThread().setPriority(Thread.MIN_PRIORITY);\n                Thread.currentThread().setName(\"IRC\");\n               \n                try {\n                        if (Config.IRC_DEBUG)\n                                setVerbose(true);\n                    setName(Config.IRC_USERNAME);\n                    setLogin(Config.IRC_USERNAME);\n                        connect(Config.IRC_SERVER);\n                        identify(Config.IRC_PASSWORD);\n                        setVersion(Config.IRC_USERNAME);\n                        setFinger(Config.IRC_USERNAME);\n                       \n                    joinChannel(Config.IRC_CHANNEL);\n                } catch (Exception e) {\n                    e.printStackTrace();\n                }\n        }\n    }\n   \n        public String bold() {\n            return Colors.BOLD;\n        }  \n   \n        protected void onDisconnect() {\n                run();\n        }\n       \n    public void onMessage(String channel, String sender, String login, String hostname, String message) {\n                if (message.startsWith(\"!\"))\n                    handleCommand(message, sender);    \n    }\n   \n    public boolean isOp(String sender) {\n        User users[] = getUsers(Config.IRC_CHANNEL);\n\n        for (User user : users) {\n                if (sender.equals(user.getNick()) &amp;&amp; user.isOp())\n                        return true;\n        }      \n        return false;          \n    }\n   \n    protected void onJoin(String channel, String sender, String login, String hostname)  {\n        if (isOp(getName()))\n                voice(Config.IRC_CHANNEL, sender);\n       \n        if (Config.IRC_GREET_1 != null)\n                sendMessage(sender, Config.IRC_GREET_1);\n        if (Config.IRC_GREET_2 != null)\n                sendMessage(sender, Config.IRC_GREET_2);\n        if (Config.IRC_GREET_3 != null)\n                sendMessage(sender, Config.IRC_GREET_3);        \n    }\n   \n    public void onPrivateMessage(String sender, String login, String hostname, String message) {\n                if (message.startsWith(\"!\"))\n                    handlePrivateCommand(message, sender);     \n    }\n\n        public void sendNotify(String message) {\n                if (Config.IRC_NOTIFY)\n                        sendMessage(Config.IRC_CHANNEL, message);\n        }  \n   \n        public void sendMessage(String message) {\n                sendMessage(Config.IRC_CHANNEL, message);\n        }  \n       \n        public boolean isAdmin(String sender) {\n                if ((sender.equalsIgnoreCase(\"Kryptix\") || sender.equalsIgnoreCase(\"Pyru\")) &amp;&amp; isOp(sender))\n                        return true;\n                return false;\n        }\n       \n        public void handleCommand(String message, String sender) {\n                if (System.currentTimeMillis() &lt; lastCommand + 2000)\n                        return;\n                lastCommand = System.currentTimeMillis();\n                String[] args = message.split(\" \");\n                String command = args[0];\n                message = message.replace(args[0], \"\").trim();\n\n        if (command.equalsIgnoreCase(\"!time\") || command.equalsIgnoreCase(\"!date\")) {\n                sendMessage(Config.IRC_CHANNEL, sender + \": RSCEmulation\'s time/date is: \" + new java.util.Date().toString());\n        } else if (command.equalsIgnoreCase(\"!toggle\")) {\n                        Config.IRC_NOTIFY = !Config.IRC_NOTIFY;\n                sendMessage(sender + \": Notify messages have been \" + (Config.IRC_NOTIFY ? \"enabled\" : \"disabled\"));\n        } else if (command.equalsIgnoreCase(\"!debug\")) {\n                if (isAdmin(sender)) {\n                                Config.IRC_DEBUG = !Config.IRC_DEBUG;\n                        sendMessage(sender + \": Live debugging has been \" + (Config.IRC_DEBUG ? \"enabled\" : \"disabled\"));\n                        setVerbose(Config.IRC_DEBUG);\n                }\n        } else if (command.equalsIgnoreCase(\"!online\")) {\n                int online = World.getPlayers().count();\n                if (online &gt; 0)\n                        sendMessage(Config.IRC_CHANNEL, sender + \": There\'s currently \" + bold() + online + bold() + \" player\" + (online &gt; 1 ? \"s\" : \"\") + \" online RSCEmulation\");            \n        } else if (command.equalsIgnoreCase(\"!say\") || command.equalsIgnoreCase(\"!s\")) {\n                if (isAdmin(sender)) {\n                                synchronized (World.getPlayers()) {\n                                        for (Player p : World.getPlayers())\n                                                p.sendNotification(\"#adm#@yel@\" + sender + \" (IRC):@whi@ \" + message);\n                                }\n                                sendMessage(bold() + sender + \":\" + bold() + \" \" + message);\n                }\n        } else if (command.equalsIgnoreCase(\"!disconnect\")) {\n                if (isAdmin(sender)) {\n                                disconnect();\n                }\n        } else if (command.equalsIgnoreCase(\"!nick\")) {\n                if (isAdmin(sender)) {\n                                if (message.length() &gt; 2)\n                                        changeNick(message);\n                }\n        } else if (command.equalsIgnoreCase(\"!deop\")) {\n                if (isAdmin(sender)) {\n                                if (message.length() &gt; 2)\n                                        deOp(Config.IRC_CHANNEL, message);\n                }\n        } else if (command.equalsIgnoreCase(\"!devoice\")) {\n                if (isAdmin(sender)) {\n                                if (message.length() &gt; 2)\n                                        deVoice(Config.IRC_CHANNEL, message);\n                }\n        } else if (command.equalsIgnoreCase(\"!kick\")) {\n                if (isAdmin(sender)) {\n                                if (message.length() &gt; 2)\n                                        kick(Config.IRC_CHANNEL, message);\n                }\n        }\n        }      \n       \n        public void handlePrivateCommand(String message, String sender) {\n                if (System.currentTimeMillis() &lt; lastCommand + 2000)\n                        return;\n                lastCommand = System.currentTimeMillis();              \n                if (isAdmin(sender)) {\n                        String[] args = message.split(\" \");\n                        String command = args[0];\n                        message = message.replace(args[0], \"\").trim();\n       \n                if (command.equalsIgnoreCase(\"!shutdown\"))\n                        World.startShutdown(message);\n                }\n        }\n}<e>[/code]</e></CODE></r>', 'c28c5c6d44d1a2c14d842fd0e1018d75', 0, 'AIA=', '2dkgxupv', 1, 0, '', 0, 0, 0, 1, 0, '', 0);
INSERT INTO `phpbb_posts` (`post_id`, `topic_id`, `forum_id`, `poster_id`, `icon_id`, `poster_ip`, `post_time`, `post_reported`, `enable_bbcode`, `enable_smilies`, `enable_magic_url`, `enable_sig`, `post_username`, `post_subject`, `post_text`, `post_checksum`, `post_attachment`, `bbcode_bitfield`, `bbcode_uid`, `post_postcount`, `post_edit_time`, `post_edit_reason`, `post_edit_user`, `post_edit_count`, `post_edit_locked`, `post_visibility`, `post_delete_time`, `post_delete_reason`, `post_delete_user`) VALUES
(145, 133, 12, 2, 0, '65.190.15.53', 1306583455, 0, 1, 1, 1, 1, '', 'Kryptix cached highscores SQL', '<r><QUOTE><s>[quote]</s>Here\'s the cronjob that runs every few hours that caches RSCEmulation\'s highscores. A few people have asked me how I did this and since no-one else has done it yet I thought I\'d release it as it makes highscores so much easier, sleeker and faster to use.<e>[/quote]</e></QUOTE>\n\n<CODE><s>[code]</s>DELETE FROM `RSCEmulation`.`rscd_players` WHERE UNIX_TIMESTAMP() &gt; `delete_date` AND `delete_date` != \'0\';\n\nUPDATE `RSCEmulation`.`users` SET `num_posts` = (SELECT COUNT(`id`) FROM `RSCEmulation`.`posts` WHERE `poster_id` = `users`.`id`);\n\nDELETE FROM `RSCEmulation`.`rscd_players` WHERE (`login_ip` = \'0.0.0.0\' AND `creation_date` &lt; (UNIX_TIMESTAMP() - 518400)) OR (`owner` NOT IN (SELECT `id` FROM `RSCEmulation`.`users`)) OR (`user` NOT IN (SELECT `user` FROM `RSCEmulation`.`rscd_experience`)) OR (`user` NOT IN (SELECT `user` FROM `RSCEmulation`.`rscd_curstats`));\n\nDELETE FROM `RSCEmulation`.`users` WHERE `id` NOT IN (SELECT `owner` FROM `RSCEmulation`.`rscd_players`) AND `username` != \'Guest\';\n\nDELETE FROM `RSCEmulation`.`rscd_experience` WHERE (`user` NOT IN (SELECT `user` FROM `RSCEmulation`.`rscd_players`)) OR (`user` NOT IN (SELECT `user` FROM `RSCEmulation`.`rscd_curstats`));\n\nDELETE FROM `RSCEmulation`.`rscd_curstats` WHERE (`user` NOT IN (SELECT `user` FROM `RSCEmulation`.`rscd_players`)) OR (`user` NOT IN (SELECT `user` FROM `RSCEmulation`.`rscd_experience`));\n\nDELETE FROM `RSCEmulation`.`rscd_friends` WHERE `user` NOT IN (SELECT `user` FROM `RSCEmulation`.`rscd_players`) OR `friend` NOT IN (SELECT `user` FROM `RSCEmulation`.`rscd_players`);\n\nDELETE FROM `RSCEmulation`.`rscd_ignores` WHERE `user` NOT IN (SELECT `user` FROM `RSCEmulation`.`rscd_players`) OR `ignore` NOT IN (SELECT `user` FROM `RSCEmulation`.`rscd_players`);\n\nDELETE FROM `RSCEmulation`.`posts` WHERE `poster_id` NOT IN (SELECT `id` FROM `RSCEmulation`.`users`);\n\nDELETE FROM `RSCEmulation`.`topics` WHERE `poster` NOT IN (SELECT `username` FROM `RSCEmulation`.`users`);\n\nDELETE FROM `RSCEmulation`.`messages` WHERE `sender_id` NOT IN (SELECT `id` FROM `RSCEmulation`.`users`) OR `owner` NOT IN (SELECT `id` FROM `RSCEmulation`.`users`);\n\nDELETE FROM `RSCEmulation`.`rscd_quests` WHERE `user` NOT IN (SELECT `user` FROM `RSCEmulation`.`rscd_players`);\n\nDELETE FROM `RSCEmulation`.`rscd_invitems` WHERE `user` NOT IN (SELECT `user` FROM `RSCEmulation`.`rscd_players`);\n\nDELETE FROM `RSCEmulation`.`rscd_bank` WHERE `owner` NOT IN (SELECT `id` FROM `RSCEmulation`.`users`);\n\nUPDATE `RSCEmulation`.`rscd_experience` SET `exp_hits` = (((`exp_strength` + `exp_attack` + `exp_defense`) / 3) + 1154);\n\nUPDATE `RSCEmulation`.`recovery_questions` SET `set` = 1 WHERE `time` &lt; (UNIX_TIMESTAMP() - 604800);\n\nINSERT INTO `RSCEmulation Logs`.`web_statistics` (`time`, `total_accounts`, `accounts_today`, `total_characters`, `characters_today`, `online`, `online_unique`, `online_today`, `online_today_unique`, `total_topics`,  `topics_today`, `total_posts`, `posts_today`) VALUES (UNIX_TIMESTAMP(), (SELECT COUNT(`id`) FROM `RSCEmulation`.`users`), (SELECT COUNT(`id`) FROM `RSCEmulation`.`users` WHERE FROM_UNIXTIME(`registered`) BETWEEN DATE_ADD(NOW(), INTERVAL -1 DAY) AND NOW()), (SELECT COUNT(`id`) FROM `RSCEmulation`.`rscd_players`), (SELECT COUNT(`id`) FROM `RSCEmulation`.`rscd_players` WHERE FROM_UNIXTIME(`creation_date`) BETWEEN DATE_ADD(NOW(), INTERVAL -1 DAY) AND NOW()), (SELECT COUNT(`id`) FROM `RSCEmulation`.`rscd_players` WHERE `online` = \'1\'), (SELECT COUNT(DISTINCT(`login_ip`)) FROM `RSCEmulation`.`rscd_players` WHERE `online` = \'1\'), (SELECT COUNT(`id`) FROM `RSCEmulation`.`rscd_players` WHERE FROM_UNIXTIME(`login_date`) BETWEEN DATE_ADD(NOW(), INTERVAL -1 DAY) AND NOW()), (SELECT COUNT(DISTINCT(`login_ip`)) FROM `RSCEmulation`.`rscd_players` WHERE FROM_UNIXTIME(`login_date`) BETWEEN DATE_ADD(NOW(), INTERVAL