lors.GREY);\n                if(super.mouseX &gt;= secondX[0] &amp;&amp; super.mouseX &lt;= secondX[1] &amp;&amp; super.mouseY &gt;= y2 &amp;&amp; super.mouseY &lt;= y2 + 16) {\n                        color8 = Colors.ORANGE;\n                        gameGraphics.drawBoxAlpha(secondX[0], y2, 140, 16, Colors.WHITE, 75);\n                        if(mouseButtonClick != 0) {\n                                mouseButtonClick = 0;\n                                handleAbuseClick(8);\n                        }\n                }\n                gameGraphics.drawText(\"Solicitation\", x, y, 0, color8);\n                gameGraphics.drawBoxEdge(thirdX[0], y2, 140, 16, Colors.GREY);\n                if(super.mouseX &gt;= thirdX[0] &amp;&amp; super.mouseX &lt;= thirdX[1] &amp;&amp; super.mouseY &gt;= y2 &amp;&amp; super.mouseY &lt;= y2 + 16) {\n                        color13 = Colors.ORANGE;\n                        gameGraphics.drawBoxAlpha(thirdX[0], y2, 140, 16, Colors.WHITE, 75);\n                        if(mouseButtonClick != 0) {\n                                mouseButtonClick = 0;\n                                handleAbuseClick(13);\n                        }\n                }\n                gameGraphics.drawText(\"Breaking real-world laws\", x3, y, 0, color13);\n                y += 19; y2 += 19;\n                gameGraphics.drawBoxEdge(40, y2, 140, 16, Colors.GREY);\n                if(super.mouseX &gt;= 40 &amp;&amp; super.mouseX &lt;= 180 &amp;&amp; super.mouseY &gt;= y2 &amp;&amp; super.mouseY &lt;= y2 + 16) {\n                        color3 = Colors.ORANGE;\n                        gameGraphics.drawBoxAlpha(40, y2, 140, 16, Colors.WHITE, 75);\n                        if(mouseButtonClick != 0) {\n                                mouseButtonClick = 0;\n                                handleAbuseClick(3);\n                        }\n                }\n                gameGraphics.drawText(\"Staff impersonation\", 110, y, 0, color3);\n                gameGraphics.drawBoxEdge(secondX[0], y2, 140, 16, Colors.GREY);\n                if(super.mouseX &gt;= secondX[0] &amp;&amp; super.mouseX &lt;= secondX[1] &amp;&amp; super.mouseY &gt;= y2 &amp;&amp; super.mouseY &lt;= y2 + 16) {\n                        color9 = Colors.ORANGE;\n                        gameGraphics.drawBoxAlpha(secondX[0], y2, 140, 16, Colors.WHITE, 75);\n                        if(mouseButtonClick != 0) {\n                                mouseButtonClick = 0;\n                                handleAbuseClick(9);\n                        }\n                }\n                gameGraphics.drawText(\"Disruptive behavior\", x, y, 0, color9);\n                gameGraphics.drawBoxEdge(thirdX[0], y2, 140, 16, Colors.GREY);\n                if(super.mouseX &gt;= thirdX[0] &amp;&amp; super.mouseX &lt;= thirdX[1] &amp;&amp; super.mouseY &gt;= y2 &amp;&amp; super.mouseY &lt;= y2 + 16) {\n                        color14 = Colors.ORANGE;\n                        gameGraphics.drawBoxAlpha(thirdX[0], y2, 140, 16, Colors.WHITE, 75);\n                        if(mouseButtonClick != 0) {\n                                mouseButtonClick = 0;\n                                handleAbuseClick(14);\n                        }\n                }\n                gameGraphics.drawText(\"Advertising websites\", x3, y, 0, color14);\n                y += 19; y2 += 19;\n                gameGraphics.drawBoxEdge(40, y2, 140, 16, Colors.GREY);\n                if(super.mouseX &gt;= 40 &amp;&amp; super.mouseX &lt;= 180 &amp;&amp; super.mouseY &gt;= y2 &amp;&amp; super.mouseY &lt;= y2 + 16) {\n                        color4 = Colors.ORANGE;\n                        gameGraphics.drawBoxAlpha(40, y2, 140, 16, Colors.WHITE, 75);\n                        if(mouseButtonClick != 0) {\n                                mouseButtonClick = 0;\n                                handleAbuseClick(4);\n                        }\n                }\n                gameGraphics.drawText(\"Macroing or use of 