                            }\n                                                else {\n                                                        owner.getActionSender().sendMessage(\"You fail to catch anything.\");\n                                                }\n                                                if(owner.getInventory().full())\n                                                        {\n                                                        owner.getActionSender().sendMessage(\"Your inventory is full.\");\n                                                        }\n                                                else\n                                                        {\n                                                        handleFishing(click);\n                                                        owner.setBusy(true);\n                                                        }\n                                                owner.setBusy(false);\n                                        }<e>[/code]</e></CODE></r>', 'ab2e3772299376bcf657d4de592db91c', 0, 'AIA=', '3oyxdpbe', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
